import{_ as s,c as a,a as p,o as t}from"./app-DPGM4BTH.js";const n={};function i(r,e){return t(),a("div",null,[...e[0]||(e[0]=[p('<h2 id="_2-重要发展时间线" tabindex="-1"><a class="header-anchor" href="#_2-重要发展时间线"><span>2. 重要发展时间线</span></a></h2><p>截止 2025 年，生成模型的发展顺序从年代上来看可以说成是：早期的神经网络 --&gt; 到深度生成模型 --&gt; 到扩散与 AIGC 大模型。</p><h3 id="_2-1-1980s-1990s" tabindex="-1"><a class="header-anchor" href="#_2-1-1980s-1990s"><span>2.1 1980s~1990s</span></a></h3><p>早期神经网络、RBM（Restricted Boltzmann Machine）、DBN（Deep Belief Network）</p><p>主要用于特征学习，还不是真正的高质量图像生成。</p><h3 id="_2-2-2010s-2020s" tabindex="-1"><a class="header-anchor" href="#_2-2-2010s-2020s"><span>2.2 2010s~2020s</span></a></h3><p>2013–2014</p><p>Autoencoder / VAE（Kingma &amp; Welling, 2013）：提出可生成新样本的概率模型。</p><p>GAN（Generative Adversarial Network, 2014）：Goodfellow 提出对抗训练，图像生成进入新阶段。</p><p>2015–2018（GAN 爆发期）</p><p>DCGAN（2015）：卷积引入 GAN，能生成清晰自然图像。</p><p>WGAN（2017）：解决训练不稳定问题。</p><p>CycleGAN（2017）：无监督图像翻译。</p><p>ProgressiveGAN（2018）：逐步生成高分辨率人脸。</p><p>StyleGAN 系列（2019–2020）：高保真、高可控人脸生成。</p><p>2016–2020（VAE 与其他方法发展）</p><p>β-VAE：控制解耦的潜在空间。</p><p>PixelCNN / PixelRNN：自回归生成模型。</p><p>2019–2020</p><p>Transformer 进入 CV：Image Transformer、VQ-VAE + Transformer</p><p>BigGAN（2019）：大规模 GAN 生成高质量 ImageNet 图像。</p><h3 id="_2-3-2020s-2025s-现在" tabindex="-1"><a class="header-anchor" href="#_2-3-2020s-2025s-现在"><span>2.3 2020s~2025s(现在)</span></a></h3><p>2020–2022（Diffusion 模型崛起）</p><p>DDPM（Ho et al., 2020）：扩散模型回归主流。</p><p>Improved DDPM、DDIM：改进采样速度。</p><p>Score-based Generative Models（Song, 2021）：统一扩散与噪声建模。</p><p>2022–至今（AIGC 时代）</p><p>DALL·E 2、Imagen：文本到图像的扩散大模型。</p><p>Stable Diffusion（2022）：开放生态，推动 AIGC 爆发。</p><p>后续发展：ControlNet、InstructPix2Pix、Video Diffusion、Sora。</p>',30)])])}const o=s(n,[["render",i]]),c=JSON.parse('{"path":"/blogs/ComputerVision/ImageGeneration/02_tuxiangxiufurumen.html","title":"图像修复入门","lang":"en-US","frontmatter":{"title":"图像修复入门","date":"2020/09/25","categories":["计算机视觉"],"tags":["图像生成","图像修复"]},"headers":[{"level":2,"title":"2. 重要发展时间线","slug":"_2-重要发展时间线","link":"#_2-重要发展时间线","children":[{"level":3,"title":"2.1 1980s~1990s","slug":"_2-1-1980s-1990s","link":"#_2-1-1980s-1990s","children":[]},{"level":3,"title":"2.2 2010s~2020s","slug":"_2-2-2010s-2020s","link":"#_2-2-2010s-2020s","children":[]},{"level":3,"title":"2.3 2020s~2025s(现在)","slug":"_2-3-2020s-2025s-现在","link":"#_2-3-2020s-2025s-现在","children":[]}]}],"git":{"createdTime":1758475598000,"updatedTime":1758475598000,"contributors":[{"name":"zjk","email":"1213860588@qq.com","commits":1}]},"filePathRelative":"blogs/ComputerVision/ImageGeneration/02_图像修复入门.md"}');export{o as comp,c as data};
